CREATE TABLE SECTEUR (SEC_CODE VARCHAR2(1) NOT NULL, SEC_LIBELLE VARCHAR2(15) NULL, PRIMARY KEY (SEC_CODE))
CREATE TABLE LABO (LAB_CODE VARCHAR2(2) NOT NULL, LAB_CHEFVENTE VARCHAR2(20) NULL, LAB_NOM VARCHAR2(10) NULL, PRIMARY KEY (LAB_CODE))
CREATE TABLE VISITEUR (VIS_MATRICULE VARCHAR2(10) NOT NULL, VIS_ADRESSE VARCHAR2(50) NULL, VIS_CP VARCHAR2(5) NULL, VIS_DATEEMBAUCHE DATE NULL, VIS_NOM VARCHAR2(25) NULL, VIS_PRENOM VARCHAR2(50) NULL, VIS_VILLE VARCHAR2(30) NULL, LAB_CODE VARCHAR2(2) NULL, SEC_CODE VARCHAR2(1) NULL, PRIMARY KEY (VIS_MATRICULE))
CREATE TABLE RAPPORT_VISITE (RAP_NUM NUMBER(38) NOT NULL, RAP_BILAN VARCHAR2(255) NULL, RAP_DATE DATE NULL, RAP_MOTIF VARCHAR2(255) NULL, VIS_MATRICULE VARCHAR2(10) NOT NULL, PRA_NUM NUMBER(38) NULL, PRIMARY KEY (RAP_NUM, VIS_MATRICULE))
CREATE TABLE MEDICAMENT (MED_DEPOTLEGAL VARCHAR2(10) NOT NULL, FAM_CODE VARCHAR2(3) NULL, MED_COMPOSITION VARCHAR2(255) NULL, MED_CONTREINDIC VARCHAR2(255) NULL, MED_EFFETS VARCHAR2(255) NULL, MED_NOMCOMMERCIAL VARCHAR2(25) NULL, MED_PRIXECHANTILLON NUMBER(19,4) NULL, PRIMARY KEY (MED_DEPOTLEGAL))
CREATE TABLE PRATICIEN (PRA_NUM NUMBER(38) NOT NULL, PRA_ADRESSE VARCHAR2(50) NULL, TYP_CODE VARCHAR2(3) NULL, PRA_COEFNOTORIETE NUMBER(19,4) NULL, PRA_CP VARCHAR2(5) NULL, PRA_NOM VARCHAR2(25) NULL, PRA_PRENOM VARCHAR2(30) NULL, PRA_VILLE VARCHAR2(25) NULL, PRIMARY KEY (PRA_NUM))
CREATE TABLE TYPE_PRATICIEN (TYP_CODE VARCHAR2(3) NOT NULL, TYP_LIBELLE VARCHAR2(25) NULL, TYP_LIEU VARCHAR2(35) NULL, PRIMARY KEY (TYP_CODE))
ALTER TABLE VISITEUR ADD CONSTRAINT FK_VISITEUR_SEC_CODE FOREIGN KEY (SEC_CODE) REFERENCES SECTEUR (SEC_CODE)
ALTER TABLE VISITEUR ADD CONSTRAINT FK_VISITEUR_LAB_CODE FOREIGN KEY (LAB_CODE) REFERENCES LABO (LAB_CODE)
ALTER TABLE RAPPORT_VISITE ADD CONSTRAINT RAPPORT_VISITE_VIS_MATRICULE FOREIGN KEY (VIS_MATRICULE) REFERENCES VISITEUR (VIS_MATRICULE)
ALTER TABLE RAPPORT_VISITE ADD CONSTRAINT FK_RAPPORT_VISITE_PRA_NUM FOREIGN KEY (PRA_NUM) REFERENCES PRATICIEN (PRA_NUM)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR2(50) NOT NULL, SEQ_COUNT NUMBER(38) NULL, PRIMARY KEY (SEQ_NAME))
